xwikidoc.content:
{{groovy}}
def context2 = xcontext.getContext();
def wikilist =  xwiki.isVirtual() ? xwiki.wikimanager.getAllWikis() : [ "xwiki" ];
def pagelist = ["Main.Spaces", "Main.Activity", "AnnotationCode.Style", "AnnotationCode.Script", "AnnotationCode.Settings"];

if (!wikilist.contains("xwiki"))
 wikilist.add(0, "xwiki")

def db = context2.getDatabase()
try {
for(wiki in wikilist) {
 def wikiname = wiki.toString()
 println "* ${wikiname}"
 context2.setDatabase(wikiname)
 for (page in pagelist) {
    println "** fixing ${page}"
    def pagedoc = xwiki.getDocument(page)
    if (request.confirm)
     pagedoc.save("Saving for programming rights")
 }
}
} finally {
 context2.setDatabase(db)
}

println ""
if (request.confirm!="1") {
  println "[[Confirm give programming rights>>$doc.fullName?confirm=1]]"
}
{{/groovy}}
