}

def Float getMySQLSpaceUsed(table, wiki) {
  def mwikiquery = (wiki=="") ? "" : "AND TABLE_SCHEMA = '${wiki}'"
  def query = (table=="") ? "SELECT SUM(DATA_LENGTH)/1024/1024 as 'space' FROM INFORMATION_SCHEMA.TABLES  WHERE 1=1 ${mwikiquery}" : "SELECT SUM(DATA_LENGTH)/1024/1024 as 'space' FROM INFORMATION_SCHEMA.TABLES  WHERE TABLE_NAME='${table}' ${mwikiquery}"
  def spaceLeftResult = runQuery(query)
  if (spaceLeftResult.size()==0 || spaceLeftResult.get(0).get("space")==null)
    return -1f
  Float spaceLeft = spaceLeftResult.get(0).get("space")
  return spaceLeft
}

def getAgeWhereClause(age) {
  return (new Date() - age + 1).format('yyyy-MM-dd')
}

// Return figures about the 2 recycle bins (-1 is for error) 
def getFigures(wiki, age) {
  def result = [ 'docNb':-1i, 'docSize':-1i, 'attNb':-1i, 'attSize':-1i]
  if (wiki=="") return result
  // Get the doc recycle bin figures
  def query = "SELECT COUNT(1) AS 'counter' FROM ${wiki}.XWIKIRECYCLEBIN WHERE XDD_DATE < '${getAgeWhereClause(age)}'"
  //println "Q:" + query
  def rs = runQuery(query)
  try {
    result.putAt('docNb', rs.get(0).get('counter').toInteger())
  } catch (Exception e) {}
  // Get the attachment recycle bin figures
  query = "SELECT COUNT(1) AS 'counter' FROM ${wiki}.XWIKIATTRECYCLEBIN WHERE XDA_DATE < '${getAgeWhereClause(age)}'"
  //println "Q:" + query
  rs = runQuery(query)
  try {
    result.putAt('attNb', rs.get(0).get('counter').toInteger())
  } catch (Exception e) {}
  return result
}

// Delete the trashed documents
def emptyDocBin(wiki, age) {
  if (wiki=="")
    return "?"
  xcontext.setDatabase(wiki)
  def workWiki = xcontext.getXWiki()
  def query = "SELECT DISTINCT XDD_FULLNAME FROM ${wiki}.XWIKIRECYCLEBIN WHERE XDD_DATE < '${getAgeWhereClause(age)}'"
  //println "Q:" + query
  def docsResult = runQuery(query)
  if (docsResult instanceof Exception)
    return
  println '**Deleted documents**'
  if (docsResult.size() > 0) {
    for (doc in docsResult) {
      def docName = doc.get('XDD_FULLNAME')
      workWiki.getDeletedDocuments(docName, '', xcontext.context).each{
        if (it.getDate()<(new Date() - age + 1)) {
          print it.fullName + ' '
          workWiki.getRecycleBinStore().deleteFromRecycleBin(
              workWiki.getDocument(it.fullName, xcontext.context),
              it.getId(),
              xcontext.context,
              true);
        }
      }
    }
  }
  xcontext.setDatabase(xcontext.getOriginalDatabase())
  println ' '
}


// Delete the trashed files
def emptyAttBin(wiki, age) {
  if (wiki=="")
    return "?"
  xcontext.setDatabase(wiki)
  def workWiki = xcontext.getXWiki()
  def context2 = xcontext.context
  def allDeletedAttachments = workWiki.getAttachmentRecycleBinStore().getAllDeletedAttachments((XWikiAttachment)null, context2, true)
  println '**Deleted files**'
  allDeletedAttachments.each{
    if (it.getDate()<(new Date() - age + 1)) {
      print it.filename + ' '
      workWiki.getAttachmentRecycleBinStore().deleteFromRecycleBin(it.id, context2, true)
    }
  }
  xcontext.setDatabase(xcontext.getOriginalDatabase())
  println ' '
}

// Include a javascript refreshing the page with a new 'n' (NewerThan) parameter
// and the related drop-down list
def printNewerWidget(current) {
  def listOfDays = [0, 1, 7, 30, 100, 366]
  if (current==null) {
    current = 30
  }
  println '\n{{html}}'
  println '''
    <script type="text/javascript">
    //<![CDATA[
    function getQueryVariable(variable) {
      var query = window.location.search.substring(1);
      var vars = query.split("&");
      for (var i=0;i<vars.length;i++) {
        var pair = vars[i].split("=");
        if (pair[0] == variable) {
          return pair[1];
        }
      }
      return "";
    }
    function sendNewer() {;
      var urlParams;
      var nbDays=document.getElementById("idNewerThan").value;
      var currentWiki=getQueryVariable("w");
      if (currentWiki.length==0) {
        urlParams = "?n=" + nbDays;
      } else {
        urlParams = "?w=" + currentWiki + "&n=" + nbDays;
      }
     window.location.assign(window.location.pathname + urlParams );
    }
    //]]>
    </script>
  '''

  println '<p>Excluding items newer than <select id="idNewerThan" onchange="sendNewer();">'
  listOfDays.each{
    print "<option value=\"${it}\" "
    if (current==it) print 'selected="selected"';
    println ">${it}</option>"
  }
  println '</select> days.</p>'
  println '\n{{/html}}\n'
} // End printNewerWidget()

def showWikiBins(wiki) {
  print '<tr><td><a href="' + doc.getName() +'?w=' + wiki + '">' + wiki
  if (wiki=="xwiki")
    println '</a> (main wiki)</td>'
  else
    println '</a></td>'
  Float sizeD = getMySQLSpaceUsed("xwikirecyclebin", wiki)
  Float sizeA = getMySQLSpaceUsed("xwikiattrecyclebin", wiki)
  println '<td style="text-align:right">' + String.format('%7.1f', sizeD) + '</td>'
  println '<td style="text-align:right">' + String.format('%7.1f', sizeA) + '</td>'
  println '<td style="text-align:right">' + String.format('%7.1f', (sizeD + sizeA)) + '</td></tr>'
}

// In a farm context, displays the size of the recycle bins for all the wikis
def displayRecycleBins() {
  if (!isMySQL()) {
    println '**Sorry, this script can\'t be run if the XWiki farm database is NOT MySQL.**'
    return
  }
  println '= Recycle Bins Size (in Mega Bytes) ='
  println '{{html}}'
  println '<table id="bins" class="grid sortable doOddEven">' 
  println '<tr class="sortHeader"><th>Wiki</th><th>Page Recycle bin</th><th>Attachment Recycle bin</th><th>Total</th></tr>'

  def wikilist = xwiki.wikimanager.getAllWikis().sort{it.toString()}
  for (wiki in wikilist) {
    showWikiBins(wiki.toString())
  }
  println '</table>'
  println '{{/html}}'
}

// The script starts here
def wParam = request.get("w") // Wiki name specified using the url parameter "w"
def nParam = request.get("n") // Exclude NewerThan specified using the url parameter "n"
Integer nValue = 30i // Default value is 30 days
if (nParam) {
  try { nValue=nParam.toInteger() } catch(exc) {}
}
if (wParam) {
  if (request.confirmation) {
    println "= Empty the 2 recycle bins of //${wParam}// ="
    emptyDocBin(wParam, nValue)
    emptyAttBin(wParam, nValue)
    println '\n\n[[Done>>' + doc.getName() + ']]'
  } else {
    def figures = getFigures(wParam, nValue)
    println "= Empty the 2 recycle bins of //${wParam}// ? ="
    printNewerWidget(nValue)
    if ((figures.docNb + figures.attNb) > 0) {
      println 'Please, confirm that you will permanently delete **' + figures.docNb + '** documents and **' + figures.attNb+ '** attachments.'
      println ' '
      println '[[{{html}}<button type="button">Yes, I confirm</button>{{/html}}>>' + doc.getName() + '?w=' + wParam + '&n=' + nValue + '&confirmation=1]]'
      println ' '
      println 'I prefer to [[go back>>' + doc.getName() + ']]'
    } else {
      println '**Nothing to delete** in the //' + wParam + '// recycle bins.'
      println ' '
      println '[[Return>>' + doc.getName() + ']]'
    }
  }
} else {
  if (!xwiki.isVirtual()) {
    response.sendRedirect(xwiki.getURL(doc.name) + '?w=' + xcontext.database)
    return
  }
  displayRecycleBins()
}
